package com.myclient.render;

import javafx.scene.image.Image;
import java.util.HashMap;

public class SpriteLoader {

    private static final HashMap<String, Image> cache = new HashMap<>();

    public static Image load(String path, int w, int h) {
        if (cache.containsKey(path)) {
            return cache.get(path);
        }
        Image img = new Image(SpriteLoader.class.getResourceAsStream(path), w, h, false, false);
        cache.put(path, img);
        return img;
    }

    public static Image[] loadFrames(String folder, int count, int w, int h) {
        Image[] frames = new Image[count];
        for (int i = 0; i < count; i++) {
            String path = folder + "/frame" + i + ".png";
            frames[i] = load(path, w, h);
        }
        return frames;
    }
}
