package client.state;

public class PlayerState {

    public int id;         // ID unik player dari server

    public double x;       // posisi pixel
    public double y;       
    public double speed;

    public int bombs;      // bomb yang tersedia
    public int bombRange;  // radius bom

    public boolean dead;   // kondisi mati
    public boolean playing; // true kalau masih di game

    public enum Dir { UP, DOWN, LEFT, RIGHT }
    public enum State { IDLE, WALK, PLACE, DEAD }

    public Dir dir = Dir.DOWN;
    public State state = State.IDLE;

    public PlayerState(int id) {
        this.id = id;
    }
}
